 fun checkout(ref: String, startPoint: String): Promise<Unit> {
  val args = mutableListOf("checkout", "--progress", "--force")
  if (startPoint != null) {
    args.add("-B")
    args.add(ref)
    args.add(startPoint)
  } else {
    args.add(ref)
  }
  return execGit(args)
}
